/* eslint-disable */
import React, { useEffect, useState, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
  useNavigate,
} from "react-router-dom";
import { motion } from "framer-motion";
import "./styles.css";

const videos = [
  { id: "1", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "2", src: "https://www.w3schools.com/html/movie.mp4" },
  { id: "3", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "4", src: "https://www.w3schools.com/html/movie.mp4" },
  { id: "5", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "6", src: "https://www.w3schools.com/html/movie.mp4" },
  { id: "7", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "8", src: "https://www.w3schools.com/html/movie.mp4" },
  { id: "9", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "10", src: "https://www.w3schools.com/html/movie.mp4" },
  { id: "11", src: "https://www.w3schools.com/html/mov_bbb.mp4" },
  { id: "12", src: "https://www.w3schools.com/html/movie.mp4" }, // NEW VIDEO
  { id: "13", src: "https://www.w3schools.com/html/mov_bbb.mp4" }, // NEW VIDEO
  { id: "14", src: "https://www.w3schools.com/html/movie.mp4" }, // NEW VIDEO
  { id: "15", src: "https://www.w3schools.com/html/mov_bbb.mp4" }  // NEW VIDEO
];

function VideoFeed() {
  const location = useLocation();
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const videoRefs = useRef([]);
  const observerRef = useRef(null);

  // On initial mount, set activeIndex based on the URL (or default to 0)
  const [activeIndex, setActiveIndex] = useState(() => {
    const videoIdFromUrl = location.pathname.replace("/video/", "");
    const index = videos.findIndex((v) => v.id === videoIdFromUrl);
    return index !== -1 ? index : 0;
  });

  // On mount (or when the URL changes externally), scroll once to the video from the URL.
  // This effect runs only on mount.
 // eslint-disable-next-line react-hooks/exhaustive-deps
useEffect(() => {
  if (videoRefs.current[activeIndex]) {
    videoRefs.current[activeIndex].scrollIntoView({
      behavior: "smooth",
      block: "start",
    });
  }
}, []);

  // Debounce URL updates: when activeIndex changes, update the URL after a delay.
  useEffect(() => {
    const timer = setTimeout(() => {
      const currentPath = `/video/${videos[activeIndex].id}`;
      if (location.pathname !== currentPath) {
        navigate(currentPath, { replace: true });
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [activeIndex, navigate, location.pathname]);

  // Manage video playback: play the active video and pause/reset all others.
  useEffect(() => {
    videoRefs.current.forEach((video, index) => {
      if (video) {
        if (index === activeIndex) {
          if (video.paused) {
            video.play().catch((error) => {
              if (error.name !== "AbortError") {
                console.error("Video play failed", error);
              }
            });
          }
        } else {
          video.pause();
          video.currentTime = 0;
        }
      }
    });
  }, [activeIndex]);

  // IntersectionObserver: update activeIndex based on which video is at the top of the viewport.
  // This observer is created once and will update the activeIndex as you scroll manually.
  useEffect(() => {
    observerRef.current = new IntersectionObserver(
      (entries) => {
        const visibleEntries = entries.filter((entry) => entry.isIntersecting);
        if (visibleEntries.length > 0) {
          visibleEntries.sort(
            (a, b) => a.boundingClientRect.top - b.boundingClientRect.top
          );
          const candidate = visibleEntries[0];
          const index = parseInt(candidate.target.dataset.index, 10);
          if (index !== activeIndex) {
            setActiveIndex(index);
          }
        }
      },
      { threshold: 0.9 }
    );

    videoRefs.current.forEach((video, index) => {
      if (video) {
        video.dataset.index = index;
        observerRef.current.observe(video);
      }
    });

    return () => {
      if (observerRef.current) observerRef.current.disconnect();
    };
  }, [activeIndex]);



  return (
    <div
      ref={containerRef}
      className="h-screen w-screen overflow-y-scroll snap-mandatory snap-y"
    >
      {videos.map((video, index) => (
        <motion.div
          key={video.id}
          className="h-screen flex justify-center items-center snap-start"
          initial={{ opacity: 0.5, scale: 1 }}
          animate={{
            opacity: activeIndex === index ? 1 : 0.5,
            scale: activeIndex === index ? 1 : 1,
          }}
          transition={{ duration: 0.3 }}
        >
          <video
            ref={(el) => (videoRefs.current[index] = el)}
            src={video.src}
            controls={false}
            muted
            loop
            /* Remove Tailwind classes */
            className=""
            /* Add inline styles */
            style={{
              width: "97.5%",
              height: "auto",
              border: "5px solid red",
              background: "black",
            }}
          />

        </motion.div>
      ))}
    </div>
  );
}


function App() {
  return (
    <Router>
      <Routes>
        <Route path="/video/:id" element={<VideoFeed />} />
        <Route path="*" element={<VideoFeed />} />
      </Routes>
    </Router>
  );
}

export default App;
