import React, { useEffect, useState, useRef } from "react";
import {
  BrowserRouter as Router,
  Routes,
  Route,
  useLocation,
  useNavigate,
} from "react-router-dom";
import { motion } from "framer-motion";
import "./styles.css";

const videos = [
  // ... (Your videos array)
];

const captions = [
  // ... (Your captions array)
];

function VideoFeed() {
  const location = useLocation();
  const navigate = useNavigate();
  const containerRef = useRef(null);
  const videoRefs = useRef([]);
  const observerRef = useRef(null);

  const [activeIndex, setActiveIndex] = useState(() => {
    const videoIdFromUrl = location.pathname.replace("/video/", "");
    const index = videos.findIndex((v) => v.id === videoIdFromUrl);
    return index !== -1 ? index : 0;
  });

  useEffect(() => {
    if (videoRefs.current[activeIndex]) {
      videoRefs.current[activeIndex].scrollIntoView({
        behavior: "smooth",
        block: "start",
      });

      // Adjust scroll position after the video is loaded
      window.scrollBy(0, -10); // Scroll 10px up to add space above the video
    }
  }, [activeIndex]);

  useEffect(() => {
    const timer = setTimeout(() => {
      const currentPath = `/video/${videos[activeIndex].id}`;
      if (location.pathname !== currentPath) {
        navigate(currentPath, { replace: true });
      }
    }, 500);
    return () => clearTimeout(timer);
  }, [activeIndex, navigate, location.pathname]);

  useEffect(() => {
    videoRefs.current.forEach((video, index) => {
      if (video) {
        if (index === activeIndex) {
          if (video.paused) {
            video.play().catch((error) => {
              if (error.name !== "AbortError") {
                console.error("Video play failed", error);
              }
            });
          }
        } else {
          video.pause();
          video.currentTime = 0;
        }
      }
    });
  }, [activeIndex]);

  useEffect(() => {
    observerRef.current = new IntersectionObserver(
      (entries) => {
        const visibleEntries = entries.filter((entry) => entry.isIntersecting);
        if (visibleEntries.length > 0) {
          visibleEntries.sort(
            (a, b) => a.boundingClientRect.top - b.boundingClientRect.top
          );
          const candidate = visibleEntries[0];
          const index = parseInt(candidate.target.dataset.index, 10);
          if (index !== activeIndex) {
            setActiveIndex(index);
          }
        }
      },
      { threshold: 0.9 }
    );

    videoRefs.current.forEach((video, index) => {
      if (video) {
        video.dataset.index = index;
        observerRef.current.observe(video);
      }
    });

    return () => {
      if (observerRef.current) observerRef.current.disconnect();
    };
  }, [activeIndex]);

  return (
    <div ref={containerRef} className="h-screen w-screen overflow-y-scroll snap-mandatory snap-y">
      {videos.map((video, index) => (
        <motion.div
          key={video.id}
          className={`video-container h-screen flex flex-col justify-center items-center snap-start`}
          style={{ marginBottom: activeIndex === index ? "80px" : "20px" }}
          initial={{ opacity: 0.5, scale: 1 }}
          animate={{ opacity: activeIndex === index ? 1 : 0.5, scale: activeIndex === index ? 1 : 1 }}
          transition={{ duration: 0.3 }}
        >
          <video
            ref={(el) => (videoRefs.current[index] = el)}
            src={video.src}
            controls={false}
            muted
            loop
          />
          <motion.div
            className="text-white text-lg text-center p-2 bg-black rounded-lg mt-2"
            initial={{ opacity: 0, y: 20 }}
            animate={activeIndex === index ? { opacity: 1, y: 0 } : { opacity: 0, y: 20 }}
            transition={{ duration: 0.5 }}
            style={{ width: "95%" }}
          >
            {captions[index]}
          </motion.div>
        </motion.div>
      ))}
    </div>
  );
}

export default function App() {
  return (
    <Router>
      <Routes>
        <Route path="/video/:id" element={<VideoFeed />} />
        <Route path="*" element={<VideoFeed />} />
      </Routes>
    </Router>
  );
}
